import React, { useState, useEffect } from 'react';
import Header from '../header/Header'; // Ganti path sesuai project kamu
import Footer from '../footer/Footer';

const About1 = () => {
  return (
    <div className="flex flex-col min-h-screen">
      <Header />
      <main className="flex-grow bg-gray-100 p-6 sm:p-8 lg:p-10 shadow-lg flex flex-col items-center">
        {/* Section: Tentang Kami */}
        <section className="w-full max-w-[1000px]">
          <h1 className="text-center mb-4 text-2xl sm:text-3xl lg:text-4xl font-bold font-arial">
            Tentang Kami
          </h1>
          <div className="flex flex-col lg:flex-row gap-6 lg:gap-10 items-center lg:items-start mt-5">
            {/* Gambar Kiri */}
            <div className="w-[180px] sm:w-[220px] lg:w-[250px] h-auto">
              <img
                src="img/ragam kaltim.png"
                alt="Logo Ragam Kaltim"
                className="w-full h-auto rounded-lg"
              />
            </div>

            {/* Deskripsi Kanan */}
            <div className="flex-1 text-sm sm:text-base text-justify text-black font-normal leading-relaxed">
              <p>
                Selamat datang di <strong>Ragam Kaltim</strong>, sebuah website informatif yang memberikan
                panduan komprehensif tentang wisata dan budaya yang ada di 10 kota dan kabupaten di
                Provinsi Kalimantan Timur. Ragam Kaltim didedikasikan untuk mempromosikan kekayaan alam
                dan keindahan wisata di Kalimantan Timur, mulai dari wisata alam, sejarah, hingga budaya lokal.
              </p>
              <p className="mt-4">
                Di website ini, kami menyajikan informasi lengkap dan mendalam tentang setiap daerah di Kalimantan Timur, memberikan wawasan berharga bagi para wisatawan yang ingin mengeksplorasi pesona alam dan budaya di kawasan ini. 
                Mulai dari pantai-pantai memesona dengan pasir putih dan air jernih, hutan lindung yang rimbun dan menjadi rumah bagi flora serta fauna langka, 
                hingga pulau-pulau eksotis yang menawarkan ketenangan dan keindahan yang tak tertandingi, semuanya kami rangkum dalam panduan yang mudah diakses. 
                Tak hanya itu, kami juga merekomendasikan berbagai tempat rekreasi dengan aktivitas seru, mulai dari wisata sejarah, kuliner khas, 
                hingga petualangan outdoor yang memacu adrenalin. Dengan panduan ini, kami berkomitmen membantu Anda merencanakan perjalanan sempurna, menyajikan tips dan rekomendasi yang dirancang khusus agar Anda dapat menikmati keindahan tersembunyi di setiap sudut Kalimantan Timur, menciptakan kenangan tak terlupakan, dan menjadikan setiap momen perjalanan sebagai pengalaman berharga.
              </p>
            </div>
          </div>
        </section>

        {/* Section: Tim Kami */}
        <section className="w-full mt-16 text-center">
          <h2 className="text-xl sm:text-2xl lg:text-3xl font-bold mb-6">Our Team</h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-2 gap-6 ml-[10px] mr-[10px] sm:ml-[50px] sm:mr-[50px] lg:ml-[320px] lg:mr-[320px]">
            {teamData.map((member) => (
              <ProfileCard key={member.name} {...member} />
            ))}
          </div>
        </section>
      </main>
      <Footer />
    </div>
  );
};

// Data anggota tim
const teamData = [
  {
    image: 'img/ardi.jpg',
    name: 'Ardi Dwi Saputra',
    nim: 'NIM 11221026',
    role: 'Frontend Developer',
    technologies: 'HTML, CSS, React.js, Python',
    description:
      'Nama saya Ardi Dwi Saputra. Saya seorang Mahasiswa Institut Teknologi Kalimantan, Program Studi Informatika. Saya hanyalah mahasiswa biasa-biasa saja.',
    linkedin: 'https://www.linkedin.com/in/ardidwi8104',
    github: 'https://www.github.com/ardidwi8104',
    instagram: 'https://www.instagram.com/ardi_dwi8104',
    whatsapp: 'https://wa.me/62895342503504',
  },
  {
    image: 'img/rianto.jpg',
    name: 'Rianto Padli',
    nim: 'NIM 11221049',
    role: 'Frontend Developer',
    technologies: 'HTML, React.js, CSS',
    description:
      'Saya seorang Mahasiswa Institut Tekhnologi Kalimantan Program Studi Informatika tahun 2022. Sekarang di semester 5.',
    linkedin: 'https://www.linkedin.com/in/rianto-padli-07b7262a0/',
    github: 'https://github.com/riantopadli11',
    instagram: 'https://www.instagram.com/_padlay',
    whatsapp: 'https://wa.me/6282157612739',
  },
  {
    image: 'img/arya.jpg',
    name: 'Arya Andhika Trisnawan',
    nim: 'NIM 11221054',
    role: 'Frontend Developer',
    technologies: 'React.js, Python',
    description:
      'Hai! saya adalah mahasiswa semester 5 Institut Teknologi Kalimantan dengan hobi mencari uang dan memiliki keahlian dalam membuat web.',
    linkedin: 'https://linkedin.com/in/arya-andhika',
    github: 'https://github.com/AryaAT551',
    instagram: 'https://www.instagram.com/arya_a.t/profilecard/?igsh=MWk4b2d5YnF1a2lpeg==',
    whatsapp: 'https://wa.me/6281549373141',
  },
  {
    image: 'img/samuel.jpg',
    name: 'Samuel Benedicto Siahaan',
    nim: 'NIM 11221064',
    role: 'Frontend Developer',
    technologies: 'React.js, HTML, CSS',
    description:
      'Saya merupakan mahasiswa di Institut Teknologi Kalimantan dari prodi Informatika semester 5. Saya tertarik pada bidang Front-End, UI/UX, dan cybersecurity.',
    linkedin: 'https://www.linkedin.com/in/samuelsiiahaan/',
    github: 'https://github.com/SamMorales11',
    instagram: 'https://www.instagram.com/samsiiahaan/',
    whatsapp: 'https://wa.me/6281545391954',
  },
];

// Komponen ProfileCard
const ProfileCard = ({ image, name, nim, role, technologies, description, linkedin, github, instagram, whatsapp }) => {
  const [isModalOpen, setModalOpen] = useState(false);

  return (
    <div className="bg-white p-4 rounded-lg shadow-lg flex flex-col items-center w-full max-w-xs mx-auto">
      <img src={image} alt={name} className="w-[200px] h-[200px] object-cover object-center rounded-md mb-3" />
      <p className="text-base font-semibold">{name}</p>
      <p className="text-sm text-gray-600">{nim}</p>
      <p className="text-sm text-gray-700 mt-2">{role}</p>
      <p className="text-xs text-gray-500">{technologies}</p>
      <button
        onClick={() => setModalOpen(true)}
        className="mt-4 px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
      >
        Detail
      </button>

      {isModalOpen && (
        <Modal
          onClose={() => setModalOpen(false)}
          name={name}
          description={description}
          linkedin={linkedin}
          github={github}
          instagram={instagram}
          whatsapp={whatsapp}
        />
      )}
    </div>
  );
};

// Komponen Modal
const Modal = ({ onClose, name, description, linkedin, github, instagram, whatsapp }) => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const timeout = setTimeout(() => setIsVisible(true), 0);
    return () => clearTimeout(timeout);
  }, []);

  const handleClose = () => {
    setIsVisible(false);
    setTimeout(onClose, 300);
  };

  return (
    <div
      className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50"
      style={{
        opacity: isVisible ? 1 : 0,
        transition: 'opacity 0.3s ease-in-out',
      }}
    >
      <div
        className="bg-white p-6 rounded-lg w-80 sm:w-96"
        style={{
          transform: isVisible ? 'scale(1)' : 'scale(0.9)',
          transition: 'transform 0.3s ease-in-out',
        }}
      >
        <h3 className="text-xl font-semibold mb-4">{name}'s Profile</h3>
        <p className="mb-4 text-sm text-gray-600">{description}</p>
        <div className="flex justify-center items-center gap-3">
          <a href={linkedin} target="_blank" rel="noopener noreferrer">
            <img src="/img/linkedin.png" alt="LinkedIn" className="h-10 w-10" />
          </a>
          <a href={github} target="_blank" rel="noopener noreferrer">
            <img src="/img/github.png" alt="GitHub" className="h-10 w-10" />
          </a>
          <a href={instagram} target="_blank" rel="noopener noreferrer">
            <img src="/img/ig.png" alt="Instagram" className="h-10 w-10" />
          </a>
          <a href={whatsapp} target="_blank" rel="noopener noreferrer">
            <img src="/img/wa.png" alt="WhatsApp" className="h-10 w-10" />
          </a>
        </div>
        <button
          onClick={handleClose}
          className="mt-4 px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600"
        >
          Close
        </button>
      </div>
    </div>
  );
};

export default About1;
