import React, { useState, useEffect } from 'react';
import Header from '../fitur/Header';
import Footer from '../fitur/Footer';

const About = () => {
  return (
    <div className="flex flex-col min-h-screen">
      <Header />

      {/* Section: Tentang Kami */}
      <main className="flex-grow bg-gray-100 p-6 sm:p-8 lg:p-10 shadow-lg flex flex-col items-center">
        <h1 className="text-center mb-6 text-2xl sm:text-3xl lg:text-4xl font-bold font-arial">
          Tentang Kami
        </h1>

        <div className="about-content flex flex-col lg:flex-row gap-6 lg:gap-10 items-center lg:items-start max-w-[1000px] mt-5">
          {/* Gambar kiri */}
          <div className="img-container w-[180px] sm:w-[220px] lg:w-[250px] h-auto">
            <img
              src="img/ragam kaltim.png"
              alt="Logo Ragam Kaltim"
              className="w-full h-auto rounded-lg"
            />
          </div>

          {/* Teks kanan */}
          <div className="description flex-1 text-sm sm:text-base">
            <p className="leading-relaxed text-justify text-black font-normal">
              Selamat datang di <strong>Ragam Kaltim</strong>, sebuah website informatif yang memberikan
              panduan komprehensif tentang wisata dan budaya yang ada di 10 kota dan kabupaten di
              Provinsi Kalimantan Timur.
            </p>
            <p className="mt-4 leading-relaxed text-justify text-black font-normal">
              Di website ini, kami menyajikan informasi lengkap dan mendalam tentang setiap daerah di Kalimantan Timur, memberikan wawasan berharga bagi para wisatawan. Mulai dari pantai, hutan lindung, hingga pulau eksotis, kami rangkum dalam panduan yang mudah diakses. Kami juga merekomendasikan berbagai aktivitas, mulai dari wisata sejarah, kuliner khas, hingga petualangan outdoor.
            </p>
          </div>
        </div>

        {/* Section: Our Team */}
        <div className="team-section mt-14 w-full text-center">
          <h2 className="text-xl sm:text-2xl lg:text-3xl font-bold mb-6">Our Team</h2>
          <div className="team-members grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6 justify-items-center">
            {teamData.map((member) => (
              <ProfileCard key={member.name} {...member} />
            ))}
          </div>
        </div>
      </main>

      <Footer />
    </div>
  );
};

const teamData = [
  {
    image: 'img/ardi.jpg',
    name: 'Ardi Dwi Saputra',
    nim: 'NIM 11221026',
    role: 'Frontend Developer',
    technologies: 'HTML, CSS, React.js, Python',
    description:
      'Nama saya Ardi Dwi Saputra. Saya seorang Mahasiswa Institut Teknologi Kalimantan, Program Studi Informatika.',
    linkedin: 'https://www.linkedin.com/in/ardidwi8104',
    github: 'https://www.github.com/ardidwi8104',
    instagram: 'https://www.instagram.com/ardi_dwi8104',
    whatsapp: 'https://wa.me/62895342503504',
  },
  {
    image: 'img/rianto.jpg',
    name: 'Rianto Padli',
    nim: 'NIM 11221049',
    role: 'Frontend Developer',
    technologies: 'HTML, React.js, CSS',
    description:
      'Saya seorang Mahasiswa Institut Teknologi Kalimantan Program Studi Informatika tahun 2022.',
    linkedin: 'https://www.linkedin.com/in/rianto-padli-07b7262a0/',
    github: 'https://github.com/riantopadli11',
    instagram: 'https://www.instagram.com/_padlay',
    whatsapp: 'https://wa.me/6282157612739',
  },
  {
    image: 'img/arya.jpg',
    name: 'Arya Andhika Trisnawan',
    nim: 'NIM 11221054',
    role: 'Frontend Developer',
    technologies: 'React.js, Python',
    description:
      'Saya adalah mahasiswa semester 5 Institut Teknologi Kalimantan yang suka mencari uang dan membangun web.',
    linkedin: 'https://linkedin.com/in/arya-andhika',
    github: 'https://github.com/AryaAT551',
    instagram: 'https://www.instagram.com/arya_a.t',
    whatsapp: 'https://wa.me/6281549373141',
  },
  {
    image: 'img/samuel.jpg',
    name: 'Samuel Benedicto Siahaan',
    nim: 'NIM 11221064',
    role: 'Frontend Developer',
    technologies: 'React.js, HTML, CSS',
    description:
      'Saya mahasiswa Institut Teknologi Kalimantan semester 5. Tertarik pada front-end, UI/UX, dan cybersecurity.',
    linkedin: 'https://www.linkedin.com/in/samuelsiiahaan/',
    github: 'https://github.com/SamMorales11',
    instagram: 'https://www.instagram.com/samsiiahaan/',
    whatsapp: 'https://wa.me/6281545391954',
  },
];

const ProfileCard = ({ image, name, nim, role, technologies, description, linkedin, github, instagram, whatsapp }) => {
  const [isModalOpen, setModalOpen] = useState(false);

  return (
    <div className="team-member bg-white p-4 rounded-lg shadow-lg flex flex-col items-center w-full max-w-[220px]">
      <img src={image} alt={name} className="w-[200px] h-[200px] object-cover rounded-md mb-3" />
      <p className="text-base font-semibold">{name}</p>
      <p className="text-sm text-gray-600">{nim}</p>
      <p className="text-sm text-gray-700 mt-2">{role}</p>
      <p className="text-xs text-gray-500 text-center">{technologies}</p>
      <button
        onClick={() => setModalOpen(true)}
        className="mt-4 px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600 text-sm"
      >
        Detail
      </button>

      {isModalOpen && (
        <Modal
          onClose={() => setModalOpen(false)}
          name={name}
          description={description}
          linkedin={linkedin}
          github={github}
          instagram={instagram}
          whatsapp={whatsapp}
        />
      )}
    </div>
  );
};

const Modal = ({ onClose, name, description, linkedin, github, instagram, whatsapp }) => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const timeout = setTimeout(() => setIsVisible(true), 0);
    return () => clearTimeout(timeout);
  }, []);

  const handleClose = () => {
    setIsVisible(false);
    setTimeout(onClose, 300);
  };

  return (
    <div
      className="modal-overlay fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50"
      style={{
        opacity: isVisible ? 1 : 0,
        transition: 'opacity 0.3s ease-in-out',
      }}
    >
      <div
        className="modal-content bg-white p-6 rounded-lg w-80 sm:w-96"
        style={{
          transform: isVisible ? 'scale(1)' : 'scale(0.9)',
          transition: 'transform 0.3s ease-in-out',
        }}
      >
        <h3 className="text-xl font-semibold mb-4">{name}'s Profile</h3>
        <p className="mb-4 text-sm text-gray-600 text-justify">{description}</p>
        <div className="flex justify-center items-center gap-3">
          <a href={linkedin} target="_blank" rel="noopener noreferrer">
            <img src="/img/linkedin.png" alt="LinkedIn" className="h-10 w-10" />
          </a>
          <a href={github} target="_blank" rel="noopener noreferrer">
            <img src="/img/github.png" alt="GitHub" className="h-10 w-10" />
          </a>
          <a href={instagram} target="_blank" rel="noopener noreferrer">
            <img src="/img/ig.png" alt="Instagram" className="h-10 w-10" />
          </a>
          <a href={whatsapp} target="_blank" rel="noopener noreferrer">
            <img src="/img/wa.png" alt="WhatsApp" className="h-10 w-10" />
          </a>
        </div>
        <button
          onClick={handleClose}
          className="mt-4 px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600"
        >
          Close
        </button>
      </div>
    </div>
  );
};

export default About;
